package com.soldesk.team1.contents;

public class Contents {
	String contents_small;
	String contents_program;
	String contents_purpose;
	String contents_field;
	String contents_teacher;
	String contents_curriculum;
	String contents_facilities;
	
	public Contents() {
		// TODO Auto-generated constructor stub
	}

	public Contents(String contents_small, String contents_program, String contents_purpose, String contents_field,
			String contents_teacher, String contents_curriculum, String contents_facilities) {
		super();
		this.contents_small = contents_small;
		this.contents_program = contents_program;
		this.contents_purpose = contents_purpose;
		this.contents_field = contents_field;
		this.contents_teacher = contents_teacher;
		this.contents_curriculum = contents_curriculum;
		this.contents_facilities = contents_facilities;
	}

	public String getContents_small() {
		return contents_small;
	}

	public void setContents_small(String contents_small) {
		this.contents_small = contents_small;
	}

	public String getContents_program() {
		return contents_program;
	}

	public void setContents_program(String contents_program) {
		this.contents_program = contents_program;
	}

	public String getContents_purpose() {
		return contents_purpose;
	}

	public void setContents_purpose(String contents_purpose) {
		this.contents_purpose = contents_purpose;
	}

	public String getContents_field() {
		return contents_field;
	}

	public void setContents_field(String contents_field) {
		this.contents_field = contents_field;
	}

	public String getContents_teacher() {
		return contents_teacher;
	}

	public void setContents_teacher(String contents_teacher) {
		this.contents_teacher = contents_teacher;
	}

	public String getContents_curriculum() {
		return contents_curriculum;
	}

	public void setContents_curriculum(String contents_curriculum) {
		this.contents_curriculum = contents_curriculum;
	}

	public String getContents_facilities() {
		return contents_facilities;
	}

	public void setContents_facilities(String contents_facilities) {
		this.contents_facilities = contents_facilities;
	}
	
	
}
